import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { axiosWithAuth } from '../helpers/index';
import ImageUploadModal from './ImageUploadModal';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import '../styles/navbar.scss'



const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    Width: 200,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
  button: {
    margin: theme.spacing(1),
  },
  root: {
    width: '100%',
  },
  button: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function CarForm(props) {


  const classes = useStyles();
  const [fullopen, setFullOpen] = useState(false);
  const [errorOpen, setErrorOpen] = useState(false);
  const [currentCar, setCurrentCar] = useState({})
  const [state, setState] = React.useState({
    car_type: '',
    car_make: '',
    car_model: '',
    car_nickname: '',
    car_year: ''
  });









  function getSteps() {
    return ['create car', 'Create an ad group'];
  }

  function getStepContent(step) {
    switch (step) {
      case 0:
        return 'Select campaign settings...';
      case 1:
        return 'What is an ad group anyways?';
      default:
        return 'Unknown step';
    }
  }

    const [activeStep, setActiveStep] = React.useState(0);
    const [skipped, setSkipped] = React.useState(new Set());
    const steps = getSteps();

    const isStepOptional = (step) => {
      return step === 1;
    };

    const isStepSkipped = (step) => {
      return skipped.has(step);
    };

    const handleNext = () => {
      let newSkipped = skipped;
      if (isStepSkipped(activeStep)) {
        newSkipped = new Set(newSkipped.values());
        newSkipped.delete(activeStep);
      }

      setActiveStep((prevActiveStep) => prevActiveStep + 1);
      setSkipped(newSkipped);
    };

    const handleBack = () => {
      setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleSkip = () => {
      if (!isStepOptional(activeStep)) {
        // You probably want to guard against something like this,
        // it should never occur unless someone's actively trying to break something.
        throw new Error("You can't skip a step that isn't optional.");
      }

      setActiveStep((prevActiveStep) => prevActiveStep + 1);
      setSkipped((prevSkipped) => {
        const newSkipped = new Set(prevSkipped.values());
        newSkipped.add(activeStep);
        return newSkipped;
      });
    };

    const handleReset = () => {
      setActiveStep(0);
    };


























  const handlefullOpen = () => {
    setFullOpen(true);
  };
  const handlefullClose = () => {
    setFullOpen(false);
  };

  function handleErrorClose() {
    setErrorOpen(false);
  }

  function handleErrorOpen() {
    setErrorOpen(true);
  }


  const handleChange = name => event => {
    setState({
      ...state,
      [name]: event.target.value,
    });
  };

  const onSubmitHandler = e => {
    e.preventDefault();
    axiosWithAuth()
      .post(`/cars`, state)
      .then(res => {
        setCurrentCar(res.data)
        props.fetchCars();
        // props.onClose();
        handlefullOpen()
      })
      .catch(err => {
      });
  };


  return (
    <>

      <FormControl className={classes.formControl} noValidate autoComplete="off" onSubmit={onSubmitHandler}>

      <div className={classes.root}>
      <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = <Typography variant="caption">Optional</Typography>;
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              All steps completed - you&apos;re finished
            </Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
              </Button>
              {isStepOptional(activeStep) && (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleSkip}
                  className={classes.button}
                >
                  Skip
                </Button>
              )}

              <Button
                variant="contained"
                color="primary"
                onClick={handleNext}
                className={classes.button}
              >
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>


        <InputLabel id="demo-simple-select-label">Type</InputLabel>
        <Select
          native
          name="car_type"
          className={classes.textField}
          value={state.car_type}
          onChange={handleChange('car_type')}
          inputProps={{
            name: 'type',
            id: 'outlined-type-native-simple',
          }}
        >
          <option value="" />
          <option value={"Sedan"}>Sedan</option>
          <option value={"Coupe"}>Coupe</option>
          <option value={"Van"}>Van</option>
          <option value={"SUV"}>SUV</option>
          <option value={"Truck"}>Truck</option>
          <option value={"Wagon"}>Wagon</option>
          <option value={"Convertible"}>Convertible</option>
        </Select>


        <TextField
          id="standard-basic"
          name="car_year"
          className={classes.textField}
          label="Year"
          margin="normal"
          value={state.car_year}
          name="car_make"
          onChange={handleChange('car_year')}
        />


        <TextField
          id="standard-basic"
          name="car_make"
          className={classes.textField}
          label="Make"
          margin="normal"
          value={state.car_make}
          onChange={handleChange('car_make')}
        />


        <TextField
          id="standard-basic"
          name="car_model"
          className={classes.textField}
          label="Model"
          margin="normal"
          value={state.car_model}
          onChange={handleChange('car_model')}
        />


        <TextField
          id="standard-basic"
          name="car_nickname"
          className={classes.textField}
          label="Nickname"
          margin="normal"
          value={state.car_nickname}
          onChange={handleChange('car_nickname')}
        />



        <Button
          variant="contained"
          color="primary"
          size="large"
          className={classes.button}
          onClick={onSubmitHandler}
        >
          Enter
        </Button>

      </FormControl>


      <ImageUploadModal
        isCar={true}
        onclose={props.onClose}
        car={currentCar}
        open={fullopen}
        handleClose={handlefullClose}
        onClose={handlefullClose}
      />

    </>
  );
};



export default CarForm;
