import { green } from '@material-ui/core/colors';
import CarAddModal from './CarAddModal';
import { axiosWithAuth } from '../helpers/index';
import CarCard from './CarCard'
import Grid from '@material-ui/core/Grid';



const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 500,
    position: 'relative',
    minHeight: 200,
  },
  fab: {
    position: 'absolute',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
  fabGreen: {
    color: theme.palette.common.white,
    backgroundColor: green[500],
    '&:hover': {
      backgroundColor: green[600],
    },
  }, 
  margin: {
    margin: theme.spacing(1),
    boxShadow: "0 16px 19px rgba(0,0,0,0.2), 0 15px 15px rgba(0,0,0,0.2)",
    '&:hover':{
        boxShadow: "0 2px 4px rgba(0,0,0,0.25), 0 2px 2px rgba(0,0,0,0.22)"
      },
  },

}));


function CarShow(props) {
  const classes = useStyles();

  const [cars,setCars] = useState([])
  const [open, setOpen] = useState(false);
  const [errorOpen, setErrorOpen] = useState(false);

  /**
   * Modal Methods
   */

  function handleOpen() {
      setOpen(true);
  }
  function handleErrorOpen() {
      setErrorOpen(true);
  }
  function handleClose() {
      setOpen(false);
  }
  function handleErrorClose() {
      setErrorOpen(false);
  }


  useEffect(() => {

    fetchCars()

}, []);

async function fetchCars() {
  const res = await axiosWithAuth().get(`/cars`); 
  setCars(res.data);
}

    return (
      <div>

        <div >
        <Fab color="none" aria-label="add"  style={{color:"darkcyan",  outline:'0',textAlign:"center"}} className={classes.margin}>
          <AddIcon
          style={{textAlign:"center"}}
           onClick={handleOpen}
           />
        </Fab>
        </div>

        <CarAddModal
         fetchCars={fetchCars} 
          open={open}
          onClose={handleClose}
          titleText={"Car Form"}
          bodyText={""}
          redirect={"/mycars"}
          redirectText={"cars"}
          />



          <div>
          {cars.length < 1 ? <div>You have no Cars</div> :
