  const [thisImage, setThisimage] = useState('')

  const fileRef = imagesRef.child(`${props.post.id}/${props.image.file_name}`);

  useEffect(() => {
    getImg()
  }, []);

  function getImg() {
    fileRef.getMetadata().then((metadata) => {
      fileRef.getDownloadURL().then(url => {

        setThisimage(url)

