import {
    // USER_UNAUTHORIZED,

  } from '../actions/mechActions';


  const initialState = {
    loggedIn: false,

}


const mechReducers = (state = initialState, action) => {

    switch (action.type) {
            case ADD_TODO:
            return Object.assign({},state,{todos:[...state.todos,action.payload]})
            default:
            return state
        }
  };

export default mechReducers;
