        setInitialLoadPosts(res.data.data)
        setNumPages(res.data.pagination.lastPage)

      })
      .catch(err => {
        console.log(err)
      });




  }

  console.log(numPages,"pages num in posts")

  // useEffect(() => {
  //   props.fetchFilteredPosts("AllPosts",'date')
  // });
