    return (

      <>

        <Search searchPostsHandler={searchPostsHandler} />

        <Pagination numPages={numPages}/>
        <OrderPosts/>


        <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>

          {

            searchPosts.map(p => {
