/**
 * Dependencies
 */

import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import CardMedia from '@material-ui/core/CardMedia'

/**
 * Define styles
 */

const useStyles = makeStyles(theme => ({
  button: {
      margin: theme.spacing(1)
  },
  submitbutton: {
      justifyContent: 'center',
  },
  modal: {
      position: 'absolute',
      margin: '0 auto',
  },
  paper: {
    height: '101%',
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 2),
    outline: 'none',
    // display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    alignItems: "center",
    [theme.breakpoints.down('md')]: {
        width: '100%',
        height: '100%',
    },
    [theme.breakpoints.down('sm')]: {
        padding: theme.spacing(1,2,2),
        width:'100%',
        height: '100%',
    },
  },
  media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
}))

/**
 * Define component
 */

const MediatorCard = (props) => {
  const classes = useStyles();

  return (
    <>
        <Grid
          item xs={12}
          sm={props.numCars === 1 ? 12 : 6}
          md={props.numCars === 1 ? 12 : 6}
          lg={props.numCars === 1 ? 12 : 6}
          >
        <Card className={classes.paper}>
          <CardContent>

              <h5 className='card-name'> {props.car.car_nickname}</h5>
                <CardMedia
                className={classes.media}
                image=""
                title="Paella dish"
                />
              <p className="case-label">Car Type:</p> 
              <p className='card-info'>{props.car.car_type}</p>
              <p className="case-label">Car Make:</p> 
              <p className='card-info'>{props.car.car_make}</p>
              <p className="case-label">Car Model:</p>
              <p className='card-info'>{props.car.car_model}</p>
              <p className="case-label">NicKName:</p>
              <p className='card-info'> {props.car.car_nickname}</p>
              <p className="case-label">Year:</p>
              <p className='card-info'>{props.car.car_year}</p>

          </CardContent>

        </Card>
      </Grid>
    </>
  );
};

/**
 * Export component
 */

export default MediatorCard;
