import { axiosWithAuth } from '../helpers/index';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import CarFixCard from './CarFixCard'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia'
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import AllOutIcon from '@material-ui/icons/AllOut';
import InfoIcon from '@material-ui/icons/Info';
import CarModalExpand from './CarModalExpand'
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import CarImgUpload from './CarImgUpload';



/**
 *  Import styles
 */

import  '../styles/navbar.scss'


/**
 * Define component
 */


function CarMaintenceShow(props) {
    const [carFixes,setCarFixes] = useState([])
    const [modalStyle] = useState(getModalStyle);
    const [fullopen, setFullOpen] = useState(false);
    const [errorOpen, setErrorOpen] = useState(false);

    const handlefullOpen = () => {
        setFullOpen(true);
    };
    const handlefullClose = () => {
        setFullOpen(false);
    };

    function handleErrorClose() {
        setErrorOpen(false);
    }

    function handleErrorOpen() {
        setErrorOpen(true);
    }



    useEffect(() => {
        async function fetchCars() {
            const res = await axiosWithAuth().get(`/car_fix/${props.car.id}`); 
            setCarFixes(res.data);
        }
        fetchCars()
    }, []);
    return (
      <>

      {carFixes.length < 1 ? <div>You have no fixes</div> :
              <Grid 

              item xs={12}
              sm={carFixes.length === 1 ? 12 : 12}
              md={carFixes.length === 1 ? 12 : 12}
              lg={carFixes.length === 1 ? 12 : 12} 
              container direction="row" justify="space-evenly">
                  {carFixes.map(c => {
                      return (
                        <>

                        <ExpansionPanel>


                        <ExpansionPanelSummary
                        expandIcon={<ExpandMoreIcon />}
                        aria-controls="panel1a-content"
                        id="panel1a-header"
                        >
                        <h4>Details</h4>
                        </ExpansionPanelSummary>
                        <ExpansionPanelDetails>

                        <div style={{display:"flex",flexDirection:"column"}}>

                            <Toolbar style={{display:"flex",flexDirection:"row",alignItems:"spaceBetween",justifyContent:"spaceBetween"}} >

                                <Button
                                    id="edit"
                                >
                                    <EditIcon />
                                </Button>


                            <IconButton id="del"  aria-label="delete"  className={classes.margin} >
                                    <DeleteIcon  />    
                                </IconButton> 

                            </Toolbar>
                        </div>
                        </ExpansionPanelDetails>
                        </ExpansionPanel>




                        <CarFixCard carFix={c} key={caches.uid}  />
                        </>
                     );
                  })}
              </Grid>
            }
      </>
    );
};

/**
 * Export component
 */

export default CarMaintenceShow;
