import ProfileImageUpload from './ProfileImageUpload';
import React, { useState, useEffect} from "react";
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import { axiosWithAuth } from '../helpers/index';
import { Button } from 'react-bootstrap';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css';
import EditIcon from '@material-ui/icons/Edit';
import UserSettingsFormModal from './UserSettingsFormModal'

function UserSettingsLayout(props) {

    const [open, setOpen] = useState(false);
    const [errorOpen, setErrorOpen] = useState(false);



    function handleOpen() {
        setOpen(true);
    }
    function handleErrorOpen() {
        setErrorOpen(true);
    }
    function handleClose() {
        setOpen(false);
    }
    function handleErrorClose() {
        setErrorOpen(false);
    }



    const submit = (e) => {
        e.preventDefault();
            confirmAlert({
                customUI: ({ onClose }) => {
                return (
                    <div className='custom-ui'>
                    <h1>Are you sure?</h1>
                    <Button variant="secondary" onClick={onClose}>No</Button>
                    <Button
                     variant="danger"
                        onClick={() => {
                            axiosWithAuth()
                            .post(`/users/deactivate`)
                            .then(res => {  
                                onClose();
                            })
                            .catch(err => {  
                                onClose();    
                            });  
                        }}
                    >
                        Yes
                    </Button>
                    </div>
                );
                }
            });    
      };






    return (
      <>
        <div>

        <UserSettingsFormModal
            open={open}
            car={props.car}
            onClose={handleClose}
            titleText={"Maintence Form"}
            bodyText={""}
            redirect={"/settings"}
            redirectText={"cars"}
          />
          <Fab color="none" aria-label="add"  style={{color:"darkcyan",  outline:'0'}} >
                <EditICon
            onClick={handleOpen}
           />
            </Fab>


        <div><p>Delete Account</p><Button onClick={submit} variant="danger">Delete</Button></div>

        </div>

        <div>



        </div>
      </>
    );
};



export default UserSettingsLayout;
